    public function ObtenerOficialias() {
        $OficialiasController = new OficialiaController();
        $OficialiaDto = new OficialiaDTO();
        $OficialiaDto->setActivo("S");
        $listaOficialia = $OficialiasController->selectOficialia($OficialiaDto);
        $resultado = array("estatus" => "error", "mensaje" => "No hay oficialias disponibles");
        if ($listaOficialia != "") {
            $Oficialias = array();
            foreach ($listaOficialia as $oficialia) {
                $registro = array("cveOficialia" => $oficialia->getCveOficialia(), "desOficialia" => $oficialia->getDesOficilia());
                array_push($Oficialias, $registro);
            }
            $resultado = array("estatus" => "ok", "mensaje" => "Oficialias disponibles", "oficialias" => $Oficialias);
        }
        return json_encode($resultado);
    }

